<?php/* * app functions *//** * app_auto_loder *  * Atto::loaderから呼ばれます *  * @param {string} $class_name  */function app_auto_loder( $class_name ) {	}/** * app_session *  * @staticvar {boolean} $started * @param {boolean} $start * @return {boolean}  */function app_session( $start = true ) {	static $started = false;	if ( $start && !$started ) {		$started = true;		if ( stristr( $_SERVER['HTTP_USER_AGENT'], 'MSIE' ) ) {			AttoHttpHelper::setHeader( 'p3p', 'CP="ALL DSP COR PSAa PSDa OUR NOR ONL UNI COM NAV"' );		}		$current_cookie_params = session_get_cookie_params();//		session_set_cookie_params(//				$current_cookie_params['lifetime'], $current_cookie_params['path'], '.' . atto::domain()//				, $current_cookie_params['secure'], $current_cookie_params['httponly']//		);		session_set_cookie_params(				0,	// セッションはブラウザが閉じるまで有効				$current_cookie_params['path'], '.' . atto::domain()				, $current_cookie_params['secure'], $current_cookie_params['httponly']		);		session_name( 'session_name' );		return session_start();	}	else if ( !$start && $started ) {		$started = false;		session_write_close();		return true;	}	return false;}/** * app_session_start *  * atto_session_helperクラスを利用しようとしたときコールされます *  * @staticvar {boolean} $init  * @return {boolean}  */function app_session_start() {	return app_session( true );}/** * app_session_commit *  * @return {boolean}  */function app_session_commit() {	return app_session( false );}